SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_finalizar_manutencao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_manutencao_aberta_w	smallint;
nr_seq_maquina_w	bigint;


BEGIN
if (coalesce(nr_sequencia_p,0) > 0) then

	select	max(b.nr_sequencia)
	into STRICT	nr_seq_maquina_w
	from	hd_maquina_manutencao a,
		hd_maquina_dialise b
	where	a.nr_seq_maquina = b.nr_sequencia
	and	a.nr_sequencia = nr_sequencia_p;

	update	hd_maquina_manutencao
	set	dt_finalizacao	= clock_timestamp(),
		nm_usuario_fim	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;

	select	count(*)
	into STRICT	qt_manutencao_aberta_w
	from	hd_maquina_manutencao a,
		hd_maquina_dialise b
	where	a.nr_seq_maquina = b.nr_sequencia
	and	b.nr_sequencia <> nr_sequencia_p
	and	a.nr_seq_maquina = nr_seq_maquina_w
	and	coalesce(a.dt_finalizacao::text, '') = '';

	if (qt_manutencao_aberta_w = 0) then

		update	hd_maquina_dialise
		set	ie_status	= 'N',
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_maquina_w;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
