SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_finaliza_trat_pac_dialise (cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_tratamento_w	bigint;			
nr_seq_motivo_trat_w	bigint;			
			
C01 CURSOR FOR
	SELECT	nr_sequencia
	from	PACIENTE_TRATAMENTO
	where	ie_tratamento in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DDC','IHF','IHDF')
	and	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(dt_final_tratamento::text, '') = '';

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(nr_seq_motivo_trat)
	into STRICT	nr_seq_motivo_trat_w
	from	hd_parametro
	where	cd_estabelecimento = cd_estabelecimento_p;

	if (nr_seq_motivo_trat_w IS NOT NULL AND nr_seq_motivo_trat_w::text <> '') then
	
		open C01;
		loop
		fetch C01 into	
			nr_seq_tratamento_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			update	paciente_tratamento
			set	dt_final_tratamento = clock_timestamp(),
				nr_seq_motivo_fim = nr_seq_motivo_trat_w
			where	nr_sequencia = nr_seq_tratamento_w;
			end;
		end loop;
		close C01;	
		
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
