SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_gerar_gpid ( nr_seq_dialise_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE



nr_seq_ultima_dialise_w		bigint;
cd_pessoa_fisica_w		varchar(10);
qt_peso_ultima_w		real;
qt_peso_pre_w			real;
qt_gpid_w			double precision;
qt_valor_gpid_w			double precision;


BEGIN

/* Obter os dados da diálise atual */

select	cd_pessoa_fisica,
	qt_peso_pre
into STRICT	cd_pessoa_fisica_w,
	qt_peso_pre_w
from	hd_dialise
where	nr_sequencia		= nr_seq_dialise_p;


/* Pega a sequência da última diálise */

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_ultima_dialise_w
from	hd_dialise
where	nr_sequencia		< nr_seq_dialise_p
and	cd_pessoa_fisica	= cd_pessoa_fisica_w
and	(qt_peso_pre IS NOT NULL AND qt_peso_pre::text <> '');

/* Pega o peso da última diálise */

begin
select	qt_peso_pos
into STRICT	qt_peso_ultima_w
from	hd_dialise
where	nr_sequencia		= nr_seq_ultima_dialise_w;
exception
	when others then
	qt_peso_ultima_w	:= 0;
end;

if (qt_peso_ultima_w = 0) then
	qt_gpid_w		:= 0;
	qt_valor_gpid_w		:= 0;
else
	qt_gpid_w		:= ((qt_peso_pre_w/qt_peso_ultima_w)*100)-100;
	qt_valor_gpid_w		:= (qt_peso_pre_w - qt_peso_ultima_w);
end if;

update	hd_dialise
set	qt_gpid			= qt_gpid_w,
	qt_valor_gpid		= qt_valor_gpid_w
where	nr_sequencia		= nr_seq_dialise_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
