CREATE OR REPLACE PROCEDURE hd_gerar_itens_analise_agua (nr_seq_protocolo_p bigint, nr_seq_analise_agua_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_exame_w		bigint;
nr_seq_unid_med_w	varchar(10);
nr_seq_metodo_w		bigint;
ie_prioridade_w		smallint;

c01 CURSOR FOR
	SELECT	a.nr_seq_exame,
		nr_seq_unid_med
	from	lab_protocolo_ext_item a,
		exame_laboratorio b
	where	nr_seq_protocolo = nr_seq_protocolo_p
	and	a.nr_seq_exame = b.nr_seq_exame;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_exame_w,
	nr_seq_unid_med_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	coalesce(min(ie_prioridade),0)
	into STRICT	ie_prioridade_w
	from	exame_lab_metodo
	where 	nr_seq_exame = nr_seq_exame_w;

	if (ie_prioridade_w > 0) then

		select	coalesce(min(nr_seq_metodo),0)
		into STRICT	nr_seq_metodo_w
		from	exame_lab_metodo
		where 	nr_seq_exame = nr_seq_exame_w
		and	ie_prioridade = ie_prioridade_w;

	else
		select	coalesce(min(nr_seq_metodo),0)
		into STRICT	nr_seq_metodo_w
		from	exame_lab_metodo
		where 	nr_seq_exame = nr_seq_exame_w;
	end if;

	insert into hd_analise_agua_item(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_analise_agua,
					nr_seq_exame,
					nr_seq_unidade_medida,
					nr_seq_metodo)
				values (	nextval('hd_analise_agua_item_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_analise_agua_p,
					nr_seq_exame_w,
					nr_seq_unid_med_w,
					nr_seq_metodo_w
				);


	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
