CREATE OR REPLACE PROCEDURE hd_gerar_peso_pre ( nr_seq_dialise_p bigint, qt_peso_pre_p bigint, qt_soro_reposicao_p bigint, qt_soro_devolucao_p bigint, qt_pa_diast_pos_deitado_p bigint, qt_pa_diast_pos_pe_p bigint, qt_pa_diast_pre_deitado_p bigint, qt_pa_diast_pre_pe_p bigint, qt_pa_sist_pos_deitado_p bigint, qt_pa_sist_pos_pe_p bigint, qt_pa_sist_pre_deitado_p bigint, qt_pa_sist_pre_pe_p bigint, nr_seq_tipo_soro_p bigint, ds_erro_p INOUT text, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_dialise_p IS NOT NULL AND nr_seq_dialise_p::text <> '') then
	begin
	if (qt_peso_pre_p = 0) then
		ds_erro_p	:= wheb_mensagem_pck.get_texto(279932);
	elsif (qt_peso_pre_p <> 0) then
		update	hd_dialise
		set	qt_peso_pre		= qt_peso_pre_p,
			qt_soro_reposicao		= qt_soro_reposicao_p,
			qt_soro_devolucao		= qt_soro_devolucao_p,
			qt_pa_diast_pos_deitado	= qt_pa_diast_pos_deitado_p,
			qt_pa_diast_pos_pe	= qt_pa_diast_pos_pe_p,
			qt_pa_diast_pre_deitado	= qt_pa_diast_pre_deitado_p,
			qt_pa_diast_pre_pe	= qt_pa_diast_pre_pe_p,
			qt_pa_sist_pos_deitado	= qt_pa_sist_pos_deitado_p,
			qt_pa_sist_pos_pe		= qt_pa_sist_pos_pe_p,
			qt_pa_sist_pre_deitado	= qt_pa_sist_pre_deitado_p,
			qt_pa_sist_pre_pe		= qt_pa_sist_pre_pe_p,
			nr_seq_tipo_soro		= nr_seq_tipo_soro_p,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_dialise_p;

		/* Calcula o GPID */

		ds_erro_p := hd_gerar_gpid(nr_seq_dialise_p, nm_usuario_p, ds_erro_p);
		
		CALL hd_gerar_assinatura(null, null, nr_seq_dialise_p, null, null, null, null, null, null, 'IP', nm_usuario_p, 'N');

		commit;
	end if;
	end;
end if;

end;
$body$
