CREATE OR REPLACE PROCEDURE hd_gerar_profissional_rotina ( nm_usuario_p text, cd_pessoa_fisica_p text, ie_tipo_profissional_p text, cd_medico_resp_p text, control_p text, cd_medico_update_p text default null, cd_estabelecimento_p bigint DEFAULT NULL, ie_novo_registro_p text default null) AS $body$
DECLARE

				

/*
control_p:
I - Insert new record
U - Update record
*/
ie_possui_prof_w	varchar(1);
ie_possui_cad_pac_w	varchar(1);


BEGIN

if (ie_novo_registro_p = 'S') and (control_p = 'I') then
		
			insert into hd_profissional_resp(nr_sequencia,
												cd_estabelecimento,
												dt_atualizacao,
												nm_usuario,
												dt_atualizacao_nrec,
												nm_usuario_nrec,
												cd_profissional,
												dt_inicio,
												cd_pessoa_fisica,
												ie_tipo_profissional
												)values (nextval('hd_profissional_resp_seq'),
												cd_estabelecimento_p,
												clock_timestamp(),
												nm_usuario_p,
												clock_timestamp(),
												nm_usuario_p,
												cd_medico_resp_p,
												clock_timestamp(),
												cd_pessoa_fisica_p,
												ie_tipo_profissional_p);

end if;

if (coalesce(ie_novo_registro_p::text, '') = '') then
	
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_cad_pac_w
	from	hd_pac_renal_cronico
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_prof_w
	from	hd_profissional_resp
	where	cd_profissional = cd_medico_update_p
	and		cd_pessoa_fisica = cd_pessoa_fisica_p
	and		coalesce(dt_fim::text, '') = '';

	if (control_p = 'U') and (ie_possui_cad_pac_w = 'S') then

			if (ie_possui_prof_w = 'S') then
				update hd_profissional_resp
				set dt_fim = clock_timestamp()
				where (cd_profissional = cd_medico_update_p or coalesce(cd_medico_update_p::text, '') = '')
				and cd_pessoa_fisica = cd_pessoa_fisica_p
				and	coalesce(dt_fim::text, '') = ''
				and	ie_tipo_profissional = ie_tipo_profissional_p;
			
			end if;
		
			insert into hd_profissional_resp(nr_sequencia,
												cd_estabelecimento,
												dt_atualizacao,
												nm_usuario,
												dt_atualizacao_nrec,
												nm_usuario_nrec,
												cd_profissional,
												dt_inicio,
												cd_pessoa_fisica,
												ie_tipo_profissional
												)values (nextval('hd_profissional_resp_seq'),
												cd_estabelecimento_p,
												clock_timestamp(),
												nm_usuario_p,
												clock_timestamp(),
												nm_usuario_p,
												cd_medico_resp_p,
												clock_timestamp(),
												cd_pessoa_fisica_p,
												ie_tipo_profissional_p);
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
