SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_importar_medicamentos ( nr_prescricao_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


cd_material_w		integer;
cd_unidade_medida_w	varchar(30);
qt_dose_w		double precision;
cd_intervalo_w		varchar(7);
ie_via_aplicacao_w	varchar(5);
ds_observacao_w		varchar(255);
ds_justificativa_w	varchar(255);
qt_dias_util_w		bigint;

C01 CURSOR FOR
	SELECT	a.cd_material,
		a.cd_unidade_medida,
		a.qt_dose,
		a.cd_intervalo,
		a.ie_via_aplicacao,
		a.ds_observacao,
		a.ds_justificativa,
		a.nr_dias_uso
	from  	medic_uso_continuo a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(dt_suspensao::text, '') = ''
	and	ie_utiliza_dialise = 'S';


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) then
	open c01;
	loop
	fetch c01 into 	cd_material_w,
			cd_unidade_medida_w,
			qt_dose_w,
			cd_intervalo_w,
			ie_via_aplicacao_w,
			ds_observacao_w,
			ds_justificativa_w,
			qt_dias_util_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		insert into prescr_material(nr_sequencia,
						nr_prescricao,
						ie_origem_inf,
						cd_material,
						cd_unidade_medida,
						cd_unidade_medida_dose,
						qt_dose,
						qt_unitaria,
						qt_material,
						ie_Agrupador,
						cd_intervalo,
						ie_via_aplicacao,
						ds_observacao,
						ds_justificativa,
						qt_dias_util,
						dt_Atualizacao,
						nm_usuario)
					values (nextval('prescr_material_seq'),
						nr_prescricao_p,
						1,
						cd_material_w,
						cd_unidade_medida_w,
						cd_unidade_medida_w,
						qt_dose_w,
						1,
						1,
						1,
						cd_intervalo_w,
						ie_via_aplicacao_w,
						ds_observacao_w,
						ds_justificativa_w,
						qt_dias_util_w,
						clock_timestamp(),
						nm_usuario_p);
		end;
	end loop;
	close c01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
