CREATE OR REPLACE PROCEDURE hd_inativar_colocar_pac ( nr_seq_dialise_p bigint, dt_instalacao_p timestamp, nr_atendimento_p bigint, nr_lista_sinais_inativar_p INOUT text, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


nr_seq_assinatura_w		hd_assinatura_digital.nr_sequencia%type;
nr_lista_sinais_inativar_w	varchar(255);

BEGIN

if (nr_seq_dialise_p IS NOT NULL AND nr_seq_dialise_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_assinatura_w
	from (
		SELECT	nr_sequencia
		from	hd_assinatura_digital
		where	nr_seq_dialise = nr_seq_dialise_p
		and	ie_opcao = 'CP'
		order by ie_opcao, nr_sequencia desc) alias2 LIMIT 1;

	if (nr_seq_assinatura_w IS NOT NULL AND nr_seq_assinatura_w::text <> '') then

		update	hd_assinatura_digital
		set	dt_inativacao		= clock_timestamp(),
			nm_usuario_inativacao	= nm_usuario_p
		where	nr_sequencia = nr_seq_assinatura_w;WITH RECURSIVE cte AS (


		select	max(ltrim(nr_sequencia,',')) nr_seq_sinal_vital
		into STRICT	nr_lista_sinais_inativar_w
		from (
			SELECT nr_sequencia,
			row_number() over (order by nr_sequencia) as fila
			from atendimento_sinal_vital
			where 	nr_atendimento = nr_atendimento_p
			and	ie_dialise = 'PRE'
			and	ie_situacao <> 'I'
			and	coalesce(dt_inativacao::text, '') = ''
			and	dt_sinal_vital = dt_instalacao_p) alias6 WHERE fila = 1
  UNION ALL


		select	c.nr_lista_sinais_inativar_w || ',' || max(ltrim(nr_sequencia,',')) nr_seq_sinal_vital
		into STRICT	nr_lista_sinais_inativar_w
		from (
			SELECT nr_sequencia,
			row_number() over (order by nr_sequencia) as fila
			from atendimento_sinal_vital
			where 	nr_atendimento = nr_atendimento_p
			and	ie_dialise = 'PRE'
			and	ie_situacao <> 'I'
			and	coalesce(dt_inativacao::text, '') = ''
			and	dt_sinal_vital = dt_instalacao_p) JOIN cte c ON (c.prior fila = alias6.(fila-1) and level <)

) SELECT * FROM cte WHERE connect_by_isleaf = 1;
;

		nr_lista_sinais_inativar_p := nr_lista_sinais_inativar_w;

		if (nr_lista_sinais_inativar_w IS NOT NULL AND nr_lista_sinais_inativar_w::text <> '') then

			update	atendimento_sinal_vital
			set	ie_situacao 		= 'I',
				dt_inativacao 		= clock_timestamp(),
				nm_usuario_inativacao	= nm_usuario_p
			where	nr_atendimento = nr_atendimento_p
			and	ie_dialise = 'PRE'
			and	ie_situacao <> 'I'
			and	coalesce(dt_inativacao::text, '') = ''
			and	dt_sinal_vital = dt_instalacao_p;

		end if;

	end if;

end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
