SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_inativar_reproc ( nr_seq_reproc_p bigint, nr_seq_dialisador_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_reproc_w		bigint;
qt_reuso_w		smallint;

C01 CURSOR FOR
	SELECT b.qt_reuso,
	       b.nr_sequencia
	from   hd_dializador a,
	       HD_DIALISADOR_REPROC b
	where  a.nr_sequencia = b.nr_seq_dialisador
	and    a.nr_sequencia = nr_seq_dialisador_p
	and    b.dt_inicio > ( SELECT max(c.dt_inicio)
			     from   hd_dialisador_reproc c
			     where  c.nr_sequencia = nr_seq_reproc_p
			     )
	order by 1 desc;


BEGIN
if (nr_seq_reproc_p IS NOT NULL AND nr_seq_reproc_p::text <> '') and (nr_seq_dialisador_p IS NOT NULL AND nr_seq_dialisador_p::text <> '') then

	update	HD_DIALISADOR_REPROC
	set	dt_inativacao 		= clock_timestamp(),
		nm_usuario_inativacao 	= nm_usuario_p,
		ie_situacao 		= 'I'
	where	nr_sequencia 		= nr_seq_reproc_p;

	open C01;
	loop
	fetch C01 into
		qt_reuso_w,
		nr_seq_reproc_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (nr_seq_reproc_w IS NOT NULL AND nr_seq_reproc_w::text <> '') then

			update 	HD_DIALISADOR_REPROC
			set	qt_reuso 	= qt_reuso_w - 1
			where	nr_sequencia 	= nr_seq_reproc_w;

		end if;
		end;
	end loop;
	close C01;


end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
