SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_iniciar_dialise ( nr_seq_dialise_p bigint, nr_seq_dialisador_p bigint, dt_inicio_p timestamp, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
BEGIN
 
/* Verifica se o usuário veio informado */
 
if (coalesce(nm_usuario_p::text, '') = '') then 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264227);
end if;
 
/* Atualiza data de início da diálise */
 
update 	hd_dialise 
set  	dt_inicio_dialise	= dt_inicio_p, 
	cd_pf_inicio_dialise  	= substr(obter_pessoa_fisica_usuario(nm_usuario_p,'C'),1,10) 
where	nr_sequencia      	= nr_seq_dialise_p;
 
/* Coloca o dialisador em status de uso */
 
update hd_dializador	 
set  ie_status       	= 'E' 
where nr_sequencia		= nr_seq_dialisador_p;
 
CALL hd_gerar_assinatura(null, null, nr_seq_dialise_p, null, null, null, null, null, null, 'ID', nm_usuario_p, 'N');
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
