CREATE OR REPLACE PROCEDURE hd_insere_trat_pac (cd_pessoa_fisica_p bigint, ie_tratamento_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_atendimento_p bigint) AS $body$
DECLARE

ie_pac_agudo_w	varchar(1);

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_tratamento_p IS NOT NULL AND ie_tratamento_p::text <> '') then

	ie_pac_agudo_w := obter_valor_param_usuario(7009, 141, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p);

	insert into paciente_tratamento(
		nr_sequencia,
		ie_tratamento,
		dt_inicio_tratamento,
		cd_pessoa_fisica,
		DT_ATUALIZACAO,
		nm_usuario,
		ie_paciente_agudo,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_atend_origem)
	values (	nextval('paciente_tratamento_seq'),
		ie_tratamento_p,
		clock_timestamp(),
		cd_pessoa_fisica_p,
		clock_timestamp(),
		nm_usuario_p,
		CASE WHEN ie_pac_agudo_w='S' THEN 'S'  ELSE 'N' END ,
		clock_timestamp(),
		nm_usuario_p,
		CASE WHEN nr_atendimento_p=0 THEN null  ELSE nr_atendimento_p END );
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
