CREATE OR REPLACE PROCEDURE hd_inserir_estat_controle (cd_pessoa_fisica_p text, ie_tipo_p text, ie_ordem_p bigint, ds_campo_p text, vl_campo_p text, nm_usuario_p text) AS $body$
DECLARE

/*
ie_tipo_p
	I = Insere as linhas
	A = Atualiza os dados
*/
ie_ordem_w	smallint;
ds_tipo_w	varchar(255);


C01 CURSOR FOR
	SELECT	1 ie_ordem,
		wheb_mensagem_pck.get_texto(802524) ds_tipo
	
	
union all

	SELECT	2 ie_ordem,
		wheb_mensagem_pck.get_texto(802525) ds_tipo
	
	
union all

	select	3 ie_ordem,
		wheb_mensagem_pck.get_texto(802526) ds_tipo
	
	
union all

	select	4 ie_ordem,
		wheb_mensagem_pck.get_texto(802527) ds_tipo
	
	
union all

	select	5 ie_ordem,
		wheb_mensagem_pck.get_texto(802528) ds_tipo
	;

BEGIN


if (ie_tipo_p = 'I') then
	open C01;
	loop
	fetch C01 into
		ie_ordem_w,
		ds_tipo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		insert into w_estatistica_dialise(
			nm_usuario,
			cd_pessoa_fisica,
			ie_ordem,
			ds_tipo)
		values (	nm_usuario_p,
			cd_pessoa_fisica_p,
			ie_ordem_w,
			ds_tipo_w);
		end;
	end loop;
	close C01;
elsif (ie_tipo_p = 'A') then
	CALL exec_sql_dinamico(nm_usuario_p,'update w_estatistica_dialise set '||ds_campo_p||' = '||chr(39)||vl_campo_p||chr(39)||' where ie_ordem = '||ie_ordem_p||' and nm_usuario = '||chr(39)||nm_usuario_p||chr(39)||' and cd_pessoa_fisica = '||chr(39)||cd_pessoa_fisica_p||chr(39));
end if;

commit;

end;
$body$
