CREATE OR REPLACE PROCEDURE hd_inserir_itens_solucao ( nr_seq_solucao_p bigint, nr_seq_solucao_padrao_p bigint, nm_usuario_p text) AS $body$
DECLARE

pr_solucao_w	double precision;
cd_material_w	bigint;
cd_unidade_medida_w	varchar(30);
qt_material_solucao_w	double precision;

C01 CURSOR FOR
	SELECT 	pr_solucao,
		cd_material,
		cd_unidade_medida
	from 	HD_SOLUCAO_REUSO_ITEM_PAD
	where	nr_seq_solucao_padrao = nr_seq_solucao_padrao_p;


BEGIN

select	QT_MATERIAL
into STRICT	QT_MATERIAL_solucao_w
from	HD_SOLUCAO_REUSO
where	nr_sequencia = nr_seq_solucao_p;


open C01;
loop
fetch C01 into
	pr_solucao_w,
	cd_material_w,
	cd_unidade_medida_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into HD_MATERIAL_SOLUCAO_REUSO(	NR_SEQUENCIA           ,
						DT_ATUALIZACAO,
						NM_USUARIO,
						DT_ATUALIZACAO_NREC,
						NM_USUARIO_NREC     ,
						NR_SEQ_SOLUCAO_REUSO,
						CD_UNIDADE_MEDIDA    ,
						QT_MATERIAL           ,
						CD_MATERIAL)
	values(nextval('hd_material_solucao_reuso_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_solucao_p,
		cd_unidade_medida_w,
		trunc(((qt_material_solucao_w * pr_solucao_w) /100),3),
		cd_material_w);



	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
