CREATE OR REPLACE PROCEDURE hd_liberar_paciente ( nr_seq_agenda_p bigint, cd_pessoa_fisica_p text, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


cd_convenio_w		varchar(10);


BEGIN

cd_convenio_w := obter_param_usuario(866, 4, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, cd_convenio_w);
cd_convenio_w		:= somente_numero(cd_convenio_w);

if (cd_convenio_w = '0') then
	ds_erro_p	:= wheb_mensagem_pck.get_texto(279922);
elsif (coalesce(ds_observacao_p::text, '') = '') then
	ds_erro_p	:= wheb_mensagem_pck.get_texto(279923);
elsif (nr_seq_agenda_p = 0) then
	ds_erro_p	:= wheb_mensagem_pck.get_texto(279925);
else

	update	agenda_consulta
	set	cd_convenio		= cd_convenio_w,
		cd_pessoa_fisica	= cd_pessoa_fisica_p,
		ie_status_agenda	= 'N',
		ds_observacao		= ds_observacao_p
	where	nr_sequencia		= nr_seq_agenda_p;

	commit;

end if;

end;
$body$
