CREATE OR REPLACE PROCEDURE hd_nova_dialise_hd_prescr ( cd_pessoa_fisica_p text, nr_prescricao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_possui_pac_w		varchar(1);
nr_seq_unidade_w		bigint;
ie_cateter_w		varchar(1);
nr_seq_tecnica_w		bigint;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	begin

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  ie_possui_pac
	into STRICT	ie_possui_pac_w
	from	hd_pac_renal_cronico
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	end;

end if;

if (ie_possui_pac_w = 'N') then

	begin

	select	max(nr_seq_unidade)
	into STRICT	nr_seq_unidade_w
	from	hd_parametro
	where	cd_estabelecimento = cd_estabelecimento_p;

	insert into hd_pac_renal_cronico(
			cd_estabelecimento,
			cd_pessoa_fisica,
			dt_atualizacao,
			ie_diabetico,
			nm_usuario,
			nr_seq_unid_dialise)
		values (
			cd_estabelecimento_p,
			cd_pessoa_fisica_p,
			clock_timestamp(),
			'N',
			nm_usuario_p,
			nr_seq_unidade_w);

	end;

end if;

select	max(ie_cateter)
into STRICT	ie_cateter_w
from	hd_prescricao
where	nr_prescricao = nr_prescricao_p;

if (ie_cateter_w IS NOT NULL AND ie_cateter_w::text <> '') then

	begin

	select	max(nr_sequencia)
	into STRICT	nr_seq_tecnica_w
	from	hd_tecnica_acesso
	where	ie_acesso_prescricao = ie_cateter_w;

	if (nr_seq_tecnica_w IS NOT NULL AND nr_seq_tecnica_w::text <> '') then

		insert 	into hd_acesso(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				cd_pessoa_fisica,
				nr_seq_tecnica)
		values (
				nextval('hd_acesso_seq'),
				clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_fisica_p,
				nr_seq_tecnica_w);

	end if;

	end;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
