CREATE OR REPLACE PROCEDURE hd_obter_dados_glicemia ( cd_pessoa_fisica_p text, qt_glicemia_p bigint, qt_ui_insulina_p INOUT bigint, qt_glicose_p INOUT bigint, ds_sugestao_p INOUT text) AS $body$
DECLARE


nr_prescricao_w		bigint;
nr_seq_proc_glic_w	bigint;
qt_ui_insulina_w	double precision;
qt_glicose_w		real;
ds_sugestao_w		varchar(2000);
nr_seq_glic_w		bigint;

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	max(nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_medica
	where	ie_hemodialise in ('E','R','S')
	and	(coalesce(dt_liberacao_medico, dt_liberacao) IS NOT NULL AND (coalesce(dt_liberacao_medico, dt_liberacao))::text <> '')
	and	cd_pessoa_fisica = cd_pessoa_fisica_p;

	select	max(NR_SEQ_PROT_GLIC)
	into STRICT	nr_seq_proc_glic_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_w;

	select	max(nr_sequencia)
	into STRICT	nr_seq_glic_w
	from	pep_prot_glic_nivel
	where	nr_seq_protocolo = nr_seq_proc_glic_w
	and	qt_glicemia_p between qt_glic_inic and qt_glic_fim;

	select	max(qt_ui_insulina),
		max(qt_glicose),
		max(ds_sugestao)
	into STRICT	qt_ui_insulina_w,
		qt_glicose_w,
		ds_sugestao_w
	from	pep_prot_glic_nivel
	where	nr_sequencia = nr_seq_glic_w;

	qt_ui_insulina_p := qt_ui_insulina_w;
	qt_glicose_p	 := qt_glicose_w;
	ds_sugestao_p	 := substr(ds_sugestao_w,1,255);
end if;

commit;

end;
$body$
