SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_preparar_dialisador ( nr_seq_dialisador_p bigint, dt_ultimo_preparo_p timestamp, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE

 
qt_reuso_atual_w	smallint;
qt_reuso_max_w		smallint;
					

BEGIN 
 
select	qt_reuso, 
	nr_max_reuso 
into STRICT	qt_reuso_atual_w, 
	qt_reuso_max_w 
from	hd_dializador 
where	nr_sequencia		= nr_seq_dialisador_p;
 
if	((qt_reuso_atual_w+1) > qt_reuso_max_w) then 
	ds_retorno_p		:= wheb_mensagem_pck.get_texto(799662) || '.';
end if;
 
update 	hd_dializador                 
set  	ie_status       	= 'O', 
	dt_ultimo_preparo   	= dt_ultimo_preparo_p, 
	cd_pf_ult_preparo   	= substr(obter_pessoa_fisica_usuario(nm_usuario_p,'C'),1,10) 
where	nr_sequencia      	= nr_seq_dialisador_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
