CREATE OR REPLACE PROCEDURE hd_registrar_manutencao ( nr_seq_maquina_p bigint, dt_manutencao_p timestamp, cd_pessoa_responsavel_p text, ds_motivos_manutencao_p text, ds_manutencao_p text, nm_usuario_p text) AS $body$
DECLARE


cd_setor_atendimento_w	integer;
cd_pessoa_fisica_w	varchar(10);
ds_motivos_manutencao_w	varchar(4000);
ds_codigo_temp_w	varchar(4000);
ds_codigo_aux_w		varchar(2);

k			smallint;
i			smallint;


BEGIN

if (coalesce(ds_motivos_manutencao_p::text, '') = '') then

	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264234);

elsif (coalesce(nr_seq_maquina_p,0) > 0) and (cd_pessoa_responsavel_p IS NOT NULL AND cd_pessoa_responsavel_p::text <> '') then

	ds_motivos_manutencao_w := ds_motivos_manutencao_p;
	k := 1;
	for i in k..length(ds_motivos_manutencao_w) loop
		begin

		ds_codigo_aux_w	:= substr(ds_motivos_manutencao_w,i,1);

		if (ds_codigo_aux_w = ',') then

			insert into hd_maquina_manutencao(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_responsavel,
				dt_registro,
				ds_manutencao,
				nr_seq_maquina,
				nr_seq_motivo_manutencao)
			values (nextval('hd_maquina_manutencao_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_responsavel_p,
				dt_manutencao_p,
				ds_manutencao_p,
				nr_seq_maquina_p,
				obter_somente_numero(ds_codigo_temp_w));

			ds_codigo_temp_w := '';
		else
			ds_codigo_temp_w := ds_codigo_temp_w||ds_codigo_aux_w;
		end if;
		end;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
