CREATE OR REPLACE PROCEDURE hd_registro_alteracao_reuso ( nr_seq_dialisador_p bigint, dt_alteracao_p timestamp, qt_reuso_novo_p bigint, qt_reuso_max_novo_p bigint, ds_motivo_p text, nm_usuario_p text) AS $body$
DECLARE

 
qt_reuso_atual_w	smallint;
qt_reuso_max_atual_w	bigint;


BEGIN 
 
/* Verificações */
 
if (qt_reuso_novo_p > qt_reuso_max_novo_p) then 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264229);
elsif (coalesce(ds_motivo_p::text, '') = '') then 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264231);
end if;
 
 
/* Obtem os dados do reuso atual e maximo (atual) do dialisador - Bloqueando o registro */
 
select	qt_reuso, 
	nr_max_reuso 
into STRICT	qt_reuso_atual_w, 
	qt_reuso_max_atual_w 
from	hd_dializador 
where	nr_sequencia	= nr_seq_dialisador_p 
for	update;
 
/* Insere a alteração na tabela de log */
 
insert into hd_dialisador_log( 
	nr_sequencia, 
	dt_atualizacao, 
	nm_usuario, 
	dt_atualizacao_nrec, 
	nm_usuario_nrec, 
	cd_pf_alteracao, 
	dt_alteracao, 
	nr_seq_dialisador, 
	qt_reuso_atual, 
	qt_reuso_maximo, 
	ds_motivo, 
	qt_reuso_atual_novo, 
	qt_reuso_maximo_novo 
) values ( 
	nextval('hd_dialisador_log_seq'), 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	substr(obter_pessoa_fisica_usuario(nm_usuario_p,'C'),1,10), 
	dt_alteracao_p, 
	nr_seq_dialisador_p, 
	qt_reuso_atual_w, 
	qt_reuso_max_atual_w, 
	ds_motivo_p, 
	qt_reuso_novo_p, 
	qt_reuso_max_novo_p 
);
 
/* Atualização dos dados do dialisador */
 
update	hd_dializador 
set	qt_reuso	= qt_reuso_novo_p, 
	nr_max_reuso	= qt_reuso_max_novo_p 
where	nr_sequencia	= nr_seq_dialisador_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
