CREATE OR REPLACE PROCEDURE hd_retornar_peso ( nr_seq_dialise_p bigint, cd_pessoa_fisica_p text, qt_peso_pre1_p bigint, qt_soro_reposicao_p bigint, qt_soro_devolucao_p bigint, qt_peso_pre_p INOUT bigint, qt_peso_ideal_p INOUT bigint, qt_gpid_p INOUT text, qt_valor_gpid_p INOUT text, qt_dif_peso_atual_ideal_p INOUT text, ds_titulo_p INOUT text) AS $body$
DECLARE

			    

qt_peso_pre_w		double precision;
qt_peso_ideal_w		double precision;		
qt_gpid_w		varchar(255);
qt_valor_gpid_w		varchar(255);
qt_dif_peso_atual_ideal_w varchar(255);		
			    

BEGIN

select	max(qt_peso_pre)
into STRICT	qt_peso_pre_w
from  	hd_dialise
where 	nr_sequencia = nr_seq_dialise_p;

select	max(qt_peso_ideal)
into STRICT	qt_peso_ideal_w
from 	hd_pac_renal_cronico
where 	cd_pessoa_fisica = cd_pessoa_fisica_p;

qt_gpid_w := trim(both to_char(hd_obter_gpid(nr_seq_dialise_p,cd_pessoa_fisica_p,qt_peso_pre1_p), '9990D99'));

select trim(to_char(((coalesce(qt_peso_pre1_p,0) - coalesce(qt_peso_ideal_w,0)) + (coalesce(qt_soro_reposicao_p,0)/1000) +  (coalesce(qt_soro_devolucao_p,0)/1000)), '9990D99')) qt_peso
into STRICT	qt_dif_peso_atual_ideal_w
;

if (coalesce(qt_peso_pre1_p,0) > 0) then
	qt_peso_pre_w := qt_peso_pre1_p;
end if;
qt_valor_gpid_w := trim(both to_char(hd_obter_valor_gpid(nr_seq_dialise_p,cd_pessoa_fisica_p,qt_peso_pre_w), '9990D99'));


ds_titulo_p := hd_obter_dados_dialise(nr_seq_dialise_p,'NP');

qt_peso_pre_p	  := qt_peso_pre_w;
qt_peso_ideal_p	  := qt_peso_ideal_w;
qt_gpid_p	  := qt_gpid_w;
qt_valor_gpid_p	  := qt_valor_gpid_w; 	
qt_dif_peso_atual_ideal_p := qt_dif_peso_atual_ideal_w;

commit;

end;
$body$
