CREATE OR REPLACE PROCEDURE hd_saida_paciente ( cd_pessoa_fisica_p text) AS $body$
BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	update	hd_prc_chegada
	set	dt_saida = clock_timestamp()
	where	trunc(dt_chegada) = trunc(clock_timestamp())
	and	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(dt_saida::text, '') = '';

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
