SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hd_transferir_equip_maquina ( nr_seq_maquina_p bigint, nr_seq_ponto_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_setor_atendimento_w	integer;
nr_seq_equipamento_w	bigint;
nr_seq_local_w		bigint;

nr_seq_local_antigo_w		bigint;
ds_localizacao_w		varchar(50);
ds_localizacao_antiga_w		varchar(50);
nr_seq_motivo_w			bigint;
cd_estab_contabil_w		smallint;
cd_estab_local_w		smallint;
cd_imobilizado_w		varchar(20);
ds_erro_w			varchar(100);
ie_transf_bem_equip_w		varchar(1);


BEGIN
if (nr_seq_maquina_p IS NOT NULL AND nr_seq_maquina_p::text <> '') and (nr_seq_ponto_destino_p IS NOT NULL AND nr_seq_ponto_destino_p::text <> '') then

	select	max(cd_setor_atendimento)
	into STRICT	cd_setor_atendimento_w
	from	hd_ponto_acesso
	where	nr_sequencia = nr_seq_ponto_destino_p;

	select	max(nr_seq_equipamento)
	into STRICT	nr_seq_equipamento_w
	from	hd_maquina_dialise
	where	nr_sequencia = nr_seq_maquina_p;


	if (cd_setor_atendimento_w IS NOT NULL AND cd_setor_atendimento_w::text <> '') and (nr_seq_equipamento_w IS NOT NULL AND nr_seq_equipamento_w::text <> '') then

		select	max(nr_sequencia),
			max(ds_localizacao)
		into STRICT	nr_seq_local_w,
			ds_localizacao_w
		from	man_localizacao
		where	cd_setor = cd_setor_atendimento_w;

		if (nr_seq_local_w IS NOT NULL AND nr_seq_local_w::text <> '') then

			ie_transf_bem_equip_w := obter_param_usuario(299, 442, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_transf_bem_equip_w);


			select	nr_seq_local,
				substr(obter_desc_man_localizacao(nr_seq_local),1,50),
				cd_estab_contabil,
				cd_imobilizado
			into STRICT	nr_seq_local_antigo_w,
				ds_localizacao_antiga_w,
				cd_estab_contabil_w,
				cd_imobilizado_w
			from	man_equipamento
			where	nr_sequencia = nr_seq_equipamento_w;

			select	cd_estabelecimento
			into STRICT	cd_estab_local_w
			from	man_localizacao
			where	nr_sequencia = nr_seq_local_w;

			update	man_equipamento
			set	nr_seq_local 	= nr_seq_local_w,
				dt_atualizacao	= clock_timestamp(),
				nm_usuario	= nm_usuario_p
			where	nr_sequencia	= nr_seq_equipamento_w;

			if (coalesce(cd_estab_contabil_w,0) > 0) and (coalesce(cd_estab_local_w,0) > 0) then
				update	man_equipamento
				set	cd_estab_contabil 	= cd_estab_local_w
				where	nr_sequencia	= nr_seq_equipamento_w;
			end if;

			if (coalesce(ie_transf_bem_equip_w,'N') = 'S') and (coalesce(cd_imobilizado_w,'0') <> '0') and (coalesce(nr_seq_local_w,0) <> 0) then
				ds_erro_w := gerar_transferencia_bem(cd_imobilizado_w, nr_seq_local_w, wheb_usuario_pck.get_cd_estabelecimento, nm_usuario_p, ds_erro_w);
			end if;

			select	min(nr_sequencia)
			into STRICT	nr_seq_motivo_w
			from	man_motivo_transf_equip;

			if (coalesce(nr_seq_motivo_w,0) > 0) then
				insert into man_transf_equipamento(
							nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_motivo_transf,
							nr_seq_equipamento,
							dt_transferencia,
							nr_seq_local,
							nr_seq_local_origem)
						values (	nextval('man_transf_equipamento_seq'),
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_motivo_w,
							nr_seq_equipamento_w,
							clock_timestamp(),
							nr_seq_local_w,
							nr_seq_local_antigo_w);
			end if;

			insert into man_equipamento_hist(
						nr_sequencia,
						nr_seq_equipamento,
						dt_historico,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						ds_historico,
						ie_origem)
					values (	nextval('man_equipamento_hist_seq'),
						nr_seq_equipamento_w,
						clock_timestamp(),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						wheb_mensagem_pck.get_texto(796006) || chr(13) || chr(10) ||
						wheb_mensagem_pck.get_texto(796007) || ' ' || ds_localizacao_antiga_w || chr(13) || chr(10) ||
						wheb_mensagem_pck.get_texto(796008) || ' ' || ds_localizacao_w,
						'S');


		end if;

	end if;


end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE hd_transferir_equip_maquina ( nr_seq_maquina_p bigint, nr_seq_ponto_destino_p bigint, nm_usuario_p text) FROM PUBLIC;

