CREATE OR REPLACE PROCEDURE hd_verif_material_mod_dialise ( cd_material_p bigint, nr_seq_lote_p bigint, nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
qt_material_w		bigint;
ie_prescr_dialise_w	varchar(1);	
nr_seq_mod_dialisador_w	bigint;
nr_dialisador_w	bigint;
nr_digito_verif_w	smallint;
cd_pessoa_fisica_w	varchar(10);
nr_seq_dialisador_w	bigint;
ie_material_regra_w	varchar(1);
nr_prescricao_w		bigint;		
			 

BEGIN 
 
ie_material_regra_w := 'N';
 
if (coalesce(cd_material_p,0) > 0) then 
	 
	select	max(nr_prescricao) 
	into STRICT	nr_prescricao_w 
	from	prescr_medica 
	where	nr_atendimento = nr_atendimento_p 
	and	ie_hemodialise in ('S','E') 
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
	and 	clock_timestamp() between dt_inicio_prescr and dt_validade_prescr;
	 
	if (nr_prescricao_w > 0) then 
	 
		select	max(nr_seq_mod_dialisador) 
		into STRICT	nr_seq_mod_dialisador_w 
		from	hd_prescricao 
		where	nr_prescricao = nr_prescricao_w;
		 
		select	count(*) 
		into STRICT	qt_material_w	 
		from	hd_modelo_dialisador_mat 
		where	nr_seq_modelo = nr_seq_mod_dialisador_w 
		and	cd_material = cd_material_p;
		 
		if (qt_material_w > 0) then 
			ie_material_regra_w := 'S';
		end if;
	end if;
	 
	if (ie_material_regra_w = 'S') then 
		 
		select 	nextval('dialisador_seq') 
		into STRICT	nr_dialisador_w 
		;
		 
		select	calcula_digito('MODULO11',nr_seq_dialisador_w) 
		into STRICT	nr_digito_verif_w 
		;
		 
		select 	cd_pessoa_fisica 
		into STRICT	cd_pessoa_fisica_w 
		from	prescr_medica 
		where	nr_prescricao = nr_prescricao_w;
		 
		nr_seq_dialisador_w := hd_dialisador_montar_dialse(cd_pessoa_fisica_w, nr_seq_lote_p, cd_material_p, nr_atendimento_p, nm_usuario_p, cd_estabelecimento_p, nr_seq_dialisador_w, nr_digito_verif_w, nr_dialisador_w, 'N');
		 
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
