CREATE OR REPLACE PROCEDURE hemolabor_san_gerar_cod_emp (nr_seq_emp_ent_p bigint) AS $body$
DECLARE


nr_seq_entidade_w	bigint;
nr_seq_derivado_w	bigint;
nr_seq_producao_w	bigint;

C01 CURSOR FOR
	SELECT	nr_seq_derivado,
		nr_sequencia
	from	san_producao
	where	nr_seq_emp_ent = nr_seq_emp_ent_p;


BEGIN

if (nr_seq_emp_ent_p IS NOT NULL AND nr_seq_emp_ent_p::text <> '') then

	select	max(NR_SEQ_ENTIDADE)
	into STRICT	nr_seq_entidade_w
	from	san_emprestimo
	where	nr_sequencia = nr_seq_emp_ent_p;

	open c01;
	loop
	fetch c01 into 	nr_seq_derivado_w,
			nr_seq_producao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		update	san_producao
		set	cd_barras = replace('5'||to_char( nr_seq_entidade_w ,'000')||to_char( nr_seq_producao_w ,'0000000')||to_char( nr_seq_derivado_w ,'00'),' ','')
		where	nr_sequencia = nr_seq_producao_w;

		end;
	end loop;
	close c01;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
