SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hem_copiar_info_laudo ( nr_seq_proc_p bigint, nr_seq_proc_novo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_laudo_w		bigint;
ie_pasta_w			varchar(1);

BEGIN


select	CASE WHEN max(ie_classif)='CAT' THEN 'L' WHEN max(ie_classif)='ATC' THEN 'A' WHEN max(ie_classif)='COD' THEN 'C'  ELSE '' END
into STRICT	ie_pasta_w
from	hem_proc
where	nr_sequencia = nr_seq_proc_p;

if (ie_pasta_w = 'L')	then
	select	max(nr_sequencia)
	into STRICT	nr_seq_laudo_w
	from	hem_laudo
	where	nr_seq_proc = nr_seq_proc_p;
	if (coalesce(nr_seq_laudo_w,0) > 0) then
		CALL hem_duplicar_laudo(nr_seq_laudo_w, nr_seq_proc_novo_p, nm_usuario_p);
	end if;
end if;
if (ie_pasta_w = 'C') then
	CALL hem_duplicar_laudo_congenitas(nr_seq_proc_p, nr_seq_proc_novo_p, nm_usuario_p);
end if;
if (ie_pasta_w = 'A') then
	CALL hem_duplicar_laudo_angio(nr_seq_proc_p, nr_seq_proc_novo_p, nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
