CREATE OR REPLACE PROCEDURE hem_gerar_dados_cirurgia_js (cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE

 
			 
nr_cirurgia_w bigint;
			
C01 CURSOR FOR 
	SELECT	a.nr_cirurgia 
	FROM  	cirurgia a, 
			hem_proc_interno b 
	WHERE 	a.cd_pessoa_fisica 		= cd_pessoa_fisica_p 
	AND	  	a.nr_seq_proc_interno 	= b.nr_seq_proc_interno 
	AND	  	coalesce(a.dt_fim_cirurgia::text, '') = '' 
	AND	  	NOT EXISTS (SELECT 1 FROM hem_proc c WHERE c.nr_seq_cirurgia = a.nr_cirurgia);	
			 
			 

BEGIN 
 
 
open C01;
loop 
fetch C01 into	 
	nr_cirurgia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL hem_gerar_dados_cirurgia(nr_cirurgia_w, nm_usuario_p);
		 
	 
	end;
end loop;
close C01;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
