CREATE OR REPLACE PROCEDURE hem_gerar_manometria_calculos ( nr_seq_hem_calc_p bigint, ie_avaliacao_p text, nm_usuario_p text) AS $body$
DECLARE


lista_camaras_w		dbms_sql.varchar2_table;
ds_camaras_w		varchar(255);

BEGIN
if (coalesce(nr_seq_hem_calc_p, 0) > 0) then

	if (ie_avaliacao_p = 'SI') then
			ds_camaras_w := 'AE,AD,VD,TP,CP,VE,AO,VC';

	elsif (ie_avaliacao_p = 'F') then
			ds_camaras_w := 'AD,VD,TP,CP,VE,AO';

	elsif (ie_avaliacao_p = 'AV') then
			ds_camaras_w := 'AE,AD,VD,TP,CP,VE,AO';
	end if;

	lista_camaras_w := obter_lista_string(ds_camaras_w, ',');

	for	i in lista_camaras_w.first..lista_camaras_w.last loop
		begin

		insert into hem_manometria_completa(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo,
			nr_seq_hem_calc
		) values (
			nextval('hem_manometria_completa_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			lista_camaras_w(i),
			nr_seq_hem_calc_p);
		end;
	end loop;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
