CREATE OR REPLACE PROCEDURE hem_gerar_manometria (ie_manometria_p text, qt_psve_p bigint, qt_psao_p bigint, qt_pdao_p bigint, qt_psvd_p bigint, qt_psap_p bigint, qt_pdve_p bigint, qt_pmcp_p bigint, qt_resultado_p INOUT bigint) AS $body$
BEGIN
if (ie_manometria_p = 'GVEAO') then /* Gradiente (VE-AO) */
	qt_resultado_p	:= (coalesce(qt_psve_p,0) - coalesce(qt_psao_p,0));

elsif (ie_manometria_p = 'PMAO') then /* Pressão média Ao */
	qt_resultado_p	:= (coalesce(qt_psao_p,0) + (2 * coalesce(qt_pdao_p,0))) / 3;

elsif (ie_manometria_p = 'GVDAP') then /* Gradiente (VD-AP) */
	qt_resultado_p	:= coalesce(qt_psvd_p,0) - coalesce(qt_psap_p,0);

elsif (ie_manometria_p = 'GVECP') then /* Gradiente (VE-CP) */
	qt_resultado_p	:= coalesce(qt_pdve_p,0) - coalesce(qt_pmcp_p,0);
end if;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
