CREATE OR REPLACE PROCEDURE hem_gerar_proc_iniciais ( nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_w		bigint;

BEGIN
if (coalesce(nr_cirurgia_p,0) > 0) then

	select	nextval('hem_proc_seq')
	into STRICT	nr_seq_proc_w
	;

	insert into hem_proc(
		nr_sequencia,
		cd_estabelecimento,
		cd_pessoa_fisica,
		nr_procedimento,
		dt_atualizacao,
		nm_usuario,
		nr_atendimento,
		dt_procedimento,
		cd_medico_resp,
		cd_setor_exec,
		nr_seq_procedimento,
		ie_tipo_anestesia,
		nr_seq_cirurgia
	) (
		SELECT	nr_seq_proc_w,
			a.cd_estabelecimento,
			a.cd_pessoa_fisica,
			nr_seq_proc_w,
			clock_timestamp(),
			nm_usuario_p,
			a.nr_atendimento,
			clock_timestamp(),
			b.cd_medico_resp,
			wheb_usuario_pck.get_cd_setor_atendimento,
			c.nr_seq_proc,
			a.cd_tipo_anestesia,
			a.nr_cirurgia
		from	cirurgia a,
			atendimento_paciente b,
			hem_proc_interno c
		where	a.nr_cirurgia		= nr_cirurgia_p
		and	a.nr_atendimento	= b.nr_atendimento
		and	a.nr_seq_proc_interno	= c.nr_seq_proc_interno);

	CALL hem_insert_hem_laudo(nr_seq_proc_w, nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
