CREATE OR REPLACE PROCEDURE hem_inativar_procedimento ( nr_seq_proc_p bigint, ds_motivo_inativ_p text, nm_usuario_p text) AS $body$
BEGIN
if (coalesce(nr_seq_proc_p,0) > 0) then

	update	hem_proc
	set	dt_inativacao		= clock_timestamp(),
		nm_usuario_inativacao	= nm_usuario_p,
		ds_justificativa_inat	= ds_motivo_inativ_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_proc_p;
end if;
commit;

end;
$body$
