		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ie_dominancia_p,
		cd_pessoa_fisica_p,
		'A',
    clock_timestamp(),
    cd_pessoa_usuario_w);
end if;
