SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hem_inserir_fator_risco ( lista_informacao_p text, cd_pessoa_fisica_p text) AS $body$
DECLARE


lista_informacao_w		varchar(400);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_sequencia_w			bigint;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin

	lista_informacao_w	:= lista_informacao_p;

	while	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or
		ie_contador_w > 200 loop
		begin
		tam_lista_w		:= length(lista_informacao_w);
		ie_pos_virgula_w	:= position(',' in lista_informacao_w);

		if (ie_pos_virgula_w <> 0) then
			nr_sequencia_w		:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
			lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end if;

		insert into CIH_PAC_FAT_RISCO(
			nr_sequencia,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_fisica,
			nr_seq_fator_risco,
                        cd_profissional)
		values (	nextval('cih_pac_fat_risco_seq'),
			wheb_usuario_pck.get_cd_estabelecimento,
			clock_timestamp(),
			wheb_usuario_pck.get_nm_usuario,
			clock_timestamp(),
			wheb_usuario_pck.get_nm_usuario,
			cd_pessoa_fisica_p,
			nr_sequencia_w,
                        obter_pessoa_fisica_usuario(wheb_usuario_pck.get_nm_usuario,'C'));

		ie_contador_w	:= ie_contador_w + 1;

		end;
	end loop;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
