CREATE OR REPLACE PROCEDURE hem_inserir_proc_coronaria ( lista_informacao_p text, nr_seq_proc_p integer ) AS $body$
DECLARE


lista_informacao_w		varchar(400);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_sequencia_w			bigint;

nr_seq_w			bigint;
nr_seq_vaso_w			bigint;
nr_seq_importancia_w		bigint;
nr_seq_tipo_lesao_w		bigint;
pr_obstrucao_w			bigint;
nr_seq_segmento_w		bigint;


BEGIN

if (nr_seq_proc_p IS NOT NULL AND nr_seq_proc_p::text <> '') then
	begin

	lista_informacao_w	:= lista_informacao_p;

	while	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or
		ie_contador_w > 200 loop
		begin
		tam_lista_w		:= length(lista_informacao_w);
		ie_pos_virgula_w	:= position(',' in lista_informacao_w);

		if (ie_pos_virgula_w <> 0) then
			nr_sequencia_w		:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
			lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end if;

		select	nextval('hem_coronariografia_seq')
		into STRICT	nr_seq_w
		;

		insert	into hem_coronariografia(
					nr_sequencia,
					nr_seq_vaso,
					nr_seq_importancia,
					nr_seq_tipo_lesao,
					pr_obstrucao,
					nr_seq_segmento,
					nr_seq_proc,
					dt_atualizacao,
					nm_usuario
			)(
				SELECT	nr_seq_w,
					nr_seq_vaso,
					nr_seq_importancia,
					nr_seq_tipo_lesao,
					pr_obstrucao,
					nr_seq_segmento,
					nr_seq_proc_p,
					clock_timestamp(),
					wheb_usuario_pck.get_nm_usuario
				from	hem_coronariografia
				where	nr_sequencia = nr_sequencia_w
			);

		insert	into hem_atc_quant(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					qt_comprimento_lesao,
					qt_diametro_lum_pre,
					qt_diametro_ref_prox_pre,
					qt_diametro_ref_dist_pre,
					pr_estenose_pre,
					nr_seq_coronaria
			)(
				SELECT	nextval('hem_atc_quant_seq'),
					clock_timestamp(),
					wheb_usuario_pck.get_nm_usuario,
					qt_comprimento_lesao,
					qt_diametro_lum_pre,
					qt_diametro_ref_prox_pre,
					qt_diametro_ref_dist_pre,
					pr_estenose_pre,
					nr_seq_w
				from	hem_atc_quant
				where	nr_seq_coronaria = nr_sequencia_w
			);

		ie_contador_w	:= ie_contador_w + 1;
		end;
	end loop;

	end;
end if;

end;
$body$
