SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hem_inserir_profissional (nr_seq_ativacao_p bigint, lista_informacao_p text, nm_usuario_p text) AS $body$
DECLARE


lista_informacao_w		varchar(400);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
cd_pessoa_fisica_w		varchar(10);
cd_funcao_w			bigint;
nr_sequencia_w		bigint;


BEGIN
lista_informacao_w	:= lista_informacao_p;

while	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or
	ie_contador_w > 200 loop
	begin
	tam_lista_w		:= length(lista_informacao_w);
	ie_pos_virgula_w	:= position(',' in lista_informacao_w);

	if (ie_pos_virgula_w <> 0) then
		cd_pessoa_fisica_w	:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	select	nextval('hem_proc_partic_seq')
	into STRICT	nr_sequencia_w
	;

	select	max(cd_funcao)
	into STRICT	cd_funcao_w
	from	hem_profissional
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;

	insert	into hem_proc_partic(
					nr_sequencia,
					nr_seq_proc,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					cd_pessoa_fisica,
					cd_funcao
					)
				values (
					nr_sequencia_w,
					nr_seq_ativacao_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_pessoa_fisica_w,
					cd_funcao_w
					);

	ie_contador_w	:= ie_contador_w + 1;
	end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
