SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hem_update_score_geral ( nr_seq_syntax_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_score_geral_w	real;
qt_doenca_difusa_w	real;


BEGIN
if (coalesce(nr_seq_syntax_p,0) > 0) then

	select	sum(qt_score_syntax)
	into STRICT	qt_score_geral_w
	from	hem_syntax_lesao
	where	nr_seq_syntax = nr_seq_syntax_p;

	select	count(nr_sequencia)
	into STRICT	qt_doenca_difusa_w
	from	hem_syntax_doenca_difusa
	where	nr_seq_syntax = nr_seq_syntax_p;

	update	hem_syntax
	set	cd_syntax_score = qt_score_geral_w + qt_doenca_difusa_w
	where	nr_sequencia = nr_seq_syntax_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
