CREATE OR REPLACE PROCEDURE hem_update_syntax_segmento ( nr_seq_segmento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lesao_w	bigint;


BEGIN
if (coalesce(nr_seq_segmento_p,0) > 0) then
	begin

	update	hem_syntax_segmento
	set	ie_start	= 'S'
	where	nr_sequencia = nr_seq_segmento_p;

	select	max(nr_seq_lesao)
	into STRICT	nr_seq_lesao_w
	from	hem_syntax_segmento
	where	nr_sequencia = nr_seq_segmento_p;

	update	hem_syntax_lesao
	set	ie_oclusao_cronica = 'S'
	where	nr_sequencia = nr_seq_lesao_w;
	end;
end if;

commit;

end;
$body$
