CREATE OR REPLACE PROCEDURE hfp_alterar_carga_exercicios ( nr_sequencia_p bigint, qt_valor_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_seq_aval_w		bigint;
nr_seq_etapa_w		bigint;
nr_seq_exercicio_w	bigint;

BEGIN

select	nr_seq_aval,
	nr_seq_etapa,
	nr_seq_exercicio
into STRICT	nr_seq_aval_w,
	nr_seq_etapa_w,
	nr_seq_exercicio_w
from	hfp_prescr_cargas
where	nr_sequencia = nr_sequencia_p;

update	hfp_prescr_cargas
set	qt_valor = qt_valor_p,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_seq_aval = nr_seq_aval_w
and	nr_seq_etapa = nr_seq_etapa_w
and	nr_seq_exercicio = nr_seq_exercicio_w;

commit;

end;
$body$
