CREATE OR REPLACE PROCEDURE hfp_inserir_minutos_caminhada (nr_seq_caminhada_p bigint, nr_minuto_ini_p bigint, nr_minuto_fim_p bigint, nm_usuario_p text) AS $body$
BEGIN
IF (coalesce(nr_seq_caminhada_p,0) > 0) AND (coalesce(nr_minuto_fim_p,0) < 60) THEN  --controle máximo para evitar erro de parâmetro incorreto
	FOR 	min_atual_w IN nr_minuto_ini_p..nr_minuto_fim_p LOOP
		INSERT INTO hfp_teste_cam_min(
			nr_sequencia,
			nr_seq_caminhada,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			qt_min,
			qt_distancia
		) VALUES (
			nextval('hfp_teste_cam_min_seq'),
			nr_seq_caminhada_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			min_atual_w,
			0
		);
	END LOOP;
END IF;
COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
