CREATE OR REPLACE PROCEDURE hfp_libera_avaliacao_cinesio ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_seq_paciente_w	bigint;

BEGIN
if (coalesce(nr_sequencia_p,0) > 0) then
	begin

	select	nr_seq_paciente
	into STRICT	nr_seq_paciente_w
	from	hfp_cinesio_funcional
	where	nr_sequencia = nr_sequencia_p;

	update	hfp_cinesio_funcional
	set	dt_liberacao	= clock_timestamp(),
		nm_usuario_lib	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;

	update	hfp_paciente
	set	ie_status = 'R',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_paciente_w;

	end;
end if;

commit;

end;
