CREATE OR REPLACE PROCEDURE hfp_registrar_saida ( nr_seq_avaliacao_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_sequencia_w	bigint;

BEGIN
if (nr_seq_avaliacao_p IS NOT NULL AND nr_seq_avaliacao_p::text <> '') then
	select	min(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	hfp_acesso
	where	nr_seq_aval = nr_seq_avaliacao_p
	and	coalesce(dt_saida::text, '') = '';

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	hfp_acesso
		set	dt_saida = clock_timestamp(),
			nm_usuario_saida = nm_usuario_p
		where	nr_sequencia = nr_sequencia_w;
	end if;
end if;

commit;

end;
