CREATE OR REPLACE PROCEDURE hmsl_grava_log_erro_cbl ( nr_seq_lote_p bigint, cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_tipo_erro_p text default null, dt_hora_prescr_p timestamp default null) AS $body$
DECLARE


nr_sequencia_w			log_erro_cbl.nr_sequencia%type;
cd_perfil_w				log_erro_cbl.cd_perfil%type;
nm_usuario_w			log_erro_cbl.nm_usuario%type;
cd_setor_atendimento_w	log_erro_cbl.cd_setor_atendimento%type;
ie_tipo_hora_w			log_erro_cbl.ie_tipo_hora%type;
dt_hora_chec_w			log_erro_cbl.dt_hora_chec%type;


BEGIN

if ((ie_tipo_erro_p IS NOT NULL AND ie_tipo_erro_p::text <> '') or (dt_hora_prescr_p IS NOT NULL AND dt_hora_prescr_p::text <> '')) then

	select 	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from 	log_erro_cbl;

	select 	wheb_usuario_pck.get_cd_perfil,
			wheb_usuario_pck.get_nm_usuario,
			wheb_usuario_pck.get_cd_setor_atendimento
	into STRICT	cd_perfil_w,
			nm_usuario_w,
			cd_setor_atendimento_w
	;
	
	if (cd_setor_atendimento_w = 0) then
		cd_setor_atendimento_w := null;
	end if;

	if (dt_hora_prescr_p IS NOT NULL AND dt_hora_prescr_p::text <> '') then
		dt_hora_chec_w := clock_timestamp();
		if (dt_hora_chec_w < (dt_hora_prescr_p + (30/1440)) and dt_hora_chec_w > (dt_hora_prescr_p - (30/1440))) then
			ie_tipo_hora_w := 'H';
		else
			ie_tipo_hora_w := 'A';
		end if;
	else
		dt_hora_chec_w := null;
		ie_tipo_hora_w := null;
	end if;

	insert into log_erro_cbl( nr_sequencia,
								nr_seq_lote,
								dt_atualizacao,
								nm_usuario,
								dt_atualizacao_nrec,
								nm_usuario_nrec,
								dt_hora_prescr,
								dt_hora_chec,
								cd_pessoa_fisica,
								nr_atendimento,
								cd_setor_atendimento,
								cd_perfil,
								ie_tipo_erro,
								ie_tipo_hora,
								ie_situacao	)
					  values ( nr_sequencia_w,
								nr_seq_lote_p,
								clock_timestamp(),
								nm_usuario_w,
								clock_timestamp(),
								nm_usuario_w,
								dt_hora_prescr_p,
								dt_hora_chec_w,
								cd_pessoa_fisica_p,
								nr_atendimento_p,
								cd_setor_atendimento_w,
								cd_perfil_w,
								ie_tipo_erro_p,
								ie_tipo_hora_w,
								'A'	);

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
