SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hmtl_gerar_evento_interv_farm ( nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_item_w			bigint;
nr_seq_cpoe_w			bigint;
cd_item_w				bigint;
ie_tipo_item_w			varchar(5);
nr_seq_alteracao_w		bigint;
ds_mensgem_w			varchar(255);

c01 CURSOR FOR
SELECT	nr_sequencia nr_sequencia,
		cd_material cd_item,
		'M' ie_tipo_item,
		nr_seq_mat_cpoe
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and		ie_agrupador = 1

union

select	nr_sequencia,
		cd_procedimento,
		'P' ie_tipo_item,
		nr_seq_proc_cpoe
from	prescr_procedimento
where	nr_prescricao	= nr_prescricao_p

union

select	nr_seq_solucao,
		null cd_item,
		'SOL' ie_tipo_item,
		coalesce(obter_nr_seq_cpoe_sol(nr_prescricao, nr_seq_solucao),0) nr_seq_cpoe
from	prescr_solucao
where	nr_prescricao	= nr_prescricao_p

union

select	nr_sequencia,
		cd_recomendacao,
		'R' ie_tipo_item,
		nr_seq_rec_cpoe
from	prescr_recomendacao
where	nr_prescricao	= nr_prescricao_p

union

select	nr_sequencia,
		nr_seq_gas,
		'O' ie_tipo_item,
		nr_seq_gas_cpoe
from	prescr_gasoterapia
where	nr_prescricao	= nr_prescricao_p

union

select	nr_sequencia nr_sequencia,
		cd_material cd_item,
		'S' ie_tipo_item,
		nr_seq_mat_cpoe
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and		ie_agrupador = 8

union

select	nr_sequencia nr_sequencia,
		cd_dieta cd_item,
		'D' ie_tipo_item,
		nr_seq_dieta_cpoe
from	prescr_dieta
where	nr_prescricao = nr_prescricao_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_item_w,
	cd_item_w,
	ie_tipo_item_w,
	nr_seq_cpoe_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
begin

if (nr_seq_item_w IS NOT NULL AND nr_seq_item_w::text <> '') and
	((cd_item_w IS NOT NULL AND cd_item_w::text <> '') or (ie_tipo_item_w = 'SOL')) then

	CALL hmtl_atualizar_interv_farm(nr_seq_cpoe_w,nr_prescricao_p,ie_tipo_item_w);

	ds_mensgem_w := substr(obter_texto_tasy(438677,wheb_usuario_pck.get_nr_seq_idioma),1,255);
	nr_seq_alteracao_w := plt_atualizar_gerar_evento(ie_tipo_item_w, nr_prescricao_p, nr_seq_item_w, cd_item_w, 'IS', ds_mensgem_w, nr_seq_alteracao_w, nm_usuario_p);
end if;

end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
