CREATE OR REPLACE PROCEDURE hnsg_baca_atualiza_repasse (nr_seq_retorno_p bigint) AS $body$
BEGIN
 
update	procedimento_repasse a 
set	a.ie_status	= 'A', 
	a.vl_liberado	= 0 
where	a.nr_seq_procedimento in (SELECT	x.nr_seq_propaci 
		 from	convenio_retorno_glosa x, 
			convenio_retorno_item y 
		 where	y.nr_seq_retorno	= nr_seq_retorno_p 
		 and	y.nr_sequencia		= x.nr_seq_ret_item) 
and	a.ie_status	= 'R';
 
commit;
 
update	material_repasse a 
set	a.ie_status	= 'A', 
	a.vl_liberado	= 0 
where	a.nr_seq_material in (SELECT	x.nr_seq_matpaci 
		 from	convenio_retorno_glosa x, 
			convenio_retorno_item y 
		 where	y.nr_seq_retorno	= nr_seq_retorno_p 
		 and	y.nr_sequencia		= x.nr_seq_ret_item) 
and	a.ie_status	= 'R';
 
commit;
 
CALL ATUALIZAR_REPASSE_RETORNO(nr_seq_retorno_p, 'OS_203238');
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
