CREATE OR REPLACE PROCEDURE hrim_recalcular_conta_repasse () AS $body$
DECLARE

 
nr_interno_conta_w		bigint;

c01 CURSOR FOR 
SELECT	distinct a.nr_interno_conta 
from	conta_paciente a, 
	procedimento_paciente b, 
	procedimento c 
where	a.NR_INTERNO_CONTA	= b.nr_interno_conta 
and	b.cd_procedimento	= c.cd_procedimento 
and	a.nr_seq_protocolo	in ((SELECT	nr_seq_protocolo 
					from	protocolo_convenio 
					where	dt_mesano_referencia > '01/03/2009' 
					and	cd_convenio not(66,67))) 
and	c.cd_grupo_proc in (10006,20001);


BEGIN 
 
/*insert into logxxx_tasy 
	(DT_ATUALIZACAO, 
	NM_USUARIO, 
	CD_LOG, 
	DS_LOG) 
values	(sysdate, 
	'OS_142094', 
	55754, 
	'Executado baca HRIM_RECALCULAR_CONTA_REPASSE (OS_142094)');*/
 
 
open c01;
loop 
fetch c01 into 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	CALL RECALCULAR_CONTA_REPASSE(nr_interno_conta_w,null,null,'OS_142094');
 
	commit;
 
end loop;
close c01;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
