SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE hsl_gravar_hist_indent_pac ( nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
nr_atendimento_w	bigint;
nr_cirurgia_w		bigint;
nr_seq_sala_cir_w	bigint;
ie_processo_p		smallint;

expressao1_w	varchar(255) := obter_desc_expressao_idioma(774219, null, wheb_usuario_pck.get_nr_seq_idioma);--Realizado a reimpressão do relatório (CATE 3350) - "HSL - Identificação do Paciente Cirúrgico". 
expressao2_w	varchar(255) := obter_desc_expressao_idioma(774220, null, wheb_usuario_pck.get_nr_seq_idioma);--Realizado a impressão do relatório (CATE 3350) - "HSL - Identificação do Paciente Cirúrgico". 
BEGIN 
 
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '' AND nr_seq_agenda_p <> 0) then 
 
	select	max(nr_atendimento), 
		max(nr_cirurgia), 
		max(nr_seq_sala_cir) 
	into STRICT	nr_atendimento_w, 
		nr_cirurgia_w, 
		nr_seq_sala_cir_w 
	from	agenda_paciente 
	where	nr_sequencia = nr_seq_agenda_p;
	 
	if (nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '' AND nr_cirurgia_w <> 0) then 
	 
		select count(*) 
		into STRICT	ie_processo_p 
		from	atend_hist_checagem_pda	 
		where	nr_cirurgia = nr_cirurgia_w 
		and	ie_processo = 'IE';
				 
		if (ie_processo_p > 0) then 
			CALL gravar_historico_pda(nr_atendimento_w,'RE',expressao1_w,nr_cirurgia_w,nr_seq_sala_cir_w,nm_usuario_p);
		else 		 
			CALL gravar_historico_pda(nr_atendimento_w,'IE',expressao2_w,nr_cirurgia_w,nr_seq_sala_cir_w,nm_usuario_p);
		end if;
		 
	end if;
	 
end if;
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
