CREATE OR REPLACE PROCEDURE html5_calcular_dif_diaria ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, cd_plano_p text, cd_tipo_acomod_convenio_p bigint, cd_tipo_acomodacao_p bigint, dt_referencia_p timestamp, ie_paciente_isolado_p text, dt_entrada_p timestamp, cd_procedimento_p INOUT bigint, ie_origem_proced_p INOUT bigint, vl_Categoria_p INOUT bigint, vl_diferenca_p INOUT bigint, ie_calculo_diferenca_p INOUT text) AS $body$
