SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE html_atualiza_atraso_agecons (nr_sequencia_p bigint, nr_seq_motivo_p bigint, ds_motivo_p text) AS $body$
BEGIN

if (coalesce(nr_seq_motivo_p::text, '') = '') and (coalesce(trim(both ds_motivo_p),'XPTO') = 'XPTO') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(750368);
else
	if (nr_seq_motivo_p IS NOT NULL AND nr_seq_motivo_p::text <> '') then
		update	agenda_consulta
		set	nr_seq_motivo_atraso 	= nr_seq_motivo_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_sequencia_p;
	end if;
	if (coalesce(trim(both ds_motivo_p),'XPTO') <> 'XPTO') then
		update	agenda_consulta
		set	ds_motivo_atraso 	= ds_motivo_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_sequencia_p;
	end if;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
