SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE html_atual_lib_etapa_checkup ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p:
L : Liberar
D : Desfazer liberação
*/
BEGIN

if (ie_opcao_p = 'L') then

	update	etapa_checkup_lib_dia
	set	ie_liberado = 'S',
		dt_liberacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'D') then

	update	etapa_checkup_lib_dia
	set	ie_liberado = 'N',
		dt_liberacao  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
