CREATE OR REPLACE PROCEDURE html_desdobrar_oc_item_entrega (nr_ordem_compra_p bigint, nr_item_oci_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
/* 
Procedure criada para o HTML5 com base na action utilizada no swing. 
Action: C:\java\Projetos\Projetos Comuns\WhebServidorSwing\src\br\com\wheb\action\ComOrd_F7\wDLGOrdemCompraAction\WDLGOrdemCompraAction.java 
*/
					 
 
dt_prevista_entrega_w	ordem_compra_item_entrega.dt_prevista_entrega%type;			
qt_prevista_entrega_w	ordem_compra_item_entrega.qt_prevista_entrega%type;
qt_total_entrega_w	ordem_compra_item_entrega.qt_prevista_entrega%type;
ds_observacao_w		ordem_compra_item_entrega.ds_observacao%type;

ds_erro_w		varchar(2000);
lista_original_w	varchar(2000);
lista_alterada_w	varchar(2000);
ie_deletar_w		varchar(1);
					
c01 CURSOR FOR 
SELECT	dt_prevista_entrega, 
	qt_prevista_entrega, 
	ds_observacao 
from	w_oc_item_entrega 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci 	= nr_item_oci_p;

c02 CURSOR FOR 
SELECT	dt_prevista_entrega, 
	qt_prevista_entrega 
from	ordem_compra_item_entrega 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci = nr_item_oci_p;

c02_w	c02%rowtype;


BEGIN 
 
qt_total_entrega_w	:= 0;
 
open C01;
loop 
fetch C01 into	 
	dt_prevista_entrega_w, 
	qt_prevista_entrega_w, 
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	ds_erro_w := CONSISTIR_DESDOBR_ENTREGA_OCI(nr_ordem_compra_p, nr_item_oci_p, qt_prevista_entrega_w, dt_prevista_entrega_w, 1, ds_erro_w);
	 
	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(798669,'DS_ERRO='||ds_erro_w);
	end if;
	 
	qt_total_entrega_w := qt_total_entrega_w + qt_prevista_entrega_w;
	 
	end;
end loop;
close C01;
 
ds_erro_w := CONSISTIR_DESDOBR_ENTREGA_OCI(nr_ordem_compra_p, nr_item_oci_p, qt_total_entrega_w, null, 2, ds_erro_w);
	 
if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(798669,'DS_ERRO='||ds_erro_w);
end if;
 
--Carrega a lista com as entregas originais. 
open C02;
loop 
fetch C02 into	 
	c02_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
	 
	lista_original_w	:= lista_original_w || 	obter_Desc_expressao(286507) ||': '|| PKG_DATE_FORMATERS.TO_VARCHAR(c02_w.dt_prevista_entrega, 'shortDate', WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO, WHEB_USUARIO_PCK.GET_NM_USUARIO) || ' '|| 
							obter_Desc_expressao(701494) ||': '|| c02_w.qt_prevista_entrega || chr(10);						
	 
	end;
end loop;
close C02;
 
ie_deletar_w := 'S';
 
open C01;
loop 
fetch C01 into	 
	dt_prevista_entrega_w, 
	qt_prevista_entrega_w, 
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	CALL DESDOBRAR_ENTREGA_ITEM_OCI(nr_ordem_compra_p, nr_item_oci_p, qt_prevista_entrega_w, dt_prevista_entrega_w, ds_observacao_w , ie_deletar_w, nm_usuario_p);
	 
	lista_alterada_w 	:= lista_alterada_w || 	obter_Desc_expressao(286507) ||': '|| PKG_DATE_FORMATERS.TO_VARCHAR(dt_prevista_entrega_w, 'shortDate', WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO, WHEB_USUARIO_PCK.GET_NM_USUARIO) || ' '|| 
							obter_Desc_expressao(701494) ||': '|| qt_prevista_entrega_w || chr(10);	
	ie_deletar_w := 'N';
	end;
end loop;
close C01;
 
CALL GRAVA_HIST_DESDOB_ENTREGA_OC(nr_ordem_compra_p, nr_item_oci_p, lista_original_w, lista_alterada_w, nm_usuario_p);
 
CALL ENVIA_COMUNIC_ALTERA_ENTR_OC(nr_ordem_compra_p, nr_item_oci_p, lista_original_w, lista_alterada_w, nm_usuario_p);
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
