CREATE OR REPLACE PROCEDURE html_desdobrar_oc_item_qtdade (nr_ordem_compra_p bigint, nr_item_oci_p bigint, nm_usuario_p text) AS $body$
DECLARE

qt_material_w		double precision;
ds_erro_w		varchar(2000);
ie_update_insert_w	varchar(1);

c01 CURSOR FOR
SELECT	qt_material
from	w_ordem_compra_item_desdob
where	nm_usuario = nm_usuario_p
and	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;


BEGIN

select	coalesce(sum(qt_material),0)
into STRICT	qt_material_w
from	w_ordem_compra_item_desdob
where	nm_usuario = nm_usuario_p
and	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

ds_erro_w := CONSISTIR_DESDOBR_ITEM_OC(nr_ordem_compra_p, nr_item_oci_p, qt_material_w, ds_erro_w);

if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(798669,'DS_ERRO='||ds_erro_w);
end if;

ie_update_insert_w := 'U';

open C01;
loop
fetch C01 into
	qt_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL DESDOBRAR_QT_ITEM_OCI(nr_ordem_compra_p, nr_item_oci_p, qt_material_w, ie_update_insert_w, nm_usuario_p);
	ie_update_insert_w := 'I';

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
